<script>

// Add the background image to the container
Highcharts.wrap(Highcharts.Chart.prototype, 'getContainer', function (proceed) {
	proceed.call(this);
	this.container.style.background =
		'url(http://www.highcharts.com/samples/graphics/sand.png)';
});

Highcharts.theme = {
	colors: ['#f45b5b', '#8085e9', '#8d4654', '#7798BF', '#aaeeee',
		'#ff0066', '#eeaaee', '#55BF3B', '#DF5353', '#7798BF', '#aaeeee'],
	chart: {
		backgroundColor: null,
		style: {
			fontFamily: 'Signika, serif'
		}
	},
	title: {
		style: {
			color: 'black',
			fontSize: '16px',
			fontWeight: 'bold'
		}
	},
	subtitle: {
		style: {
			color: 'black'
		}
	},
	tooltip: {
		borderWidth: 0
	},
	legend: {
		itemStyle: {
			fontWeight: 'bold',
			fontSize: '13px'
		}
	},
	xAxis: {
		labels: {
			style: {
				color: '#6e6e70'
			}
		}
	},
	yAxis: {
		labels: {
			style: {
				color: '#6e6e70'
			}
		}
	},
	plotOptions: {
		series: {
			shadow: true
		},
		candlestick: {
			lineColor: '#404048'
		},
		map: {
			shadow: false
		}
	},

	// Highstock specific
	navigator: {
		xAxis: {
			gridLineColor: '#D0D0D8'
		}
	},
	rangeSelector: {
		buttonTheme: {
			fill: 'white',
			stroke: '#C0C0C8',
			'stroke-width': 1,
			states: {
				select: {
					fill: '#D0D0D8'
				}
			}
		}
	},
	scrollbar: {
		trackBorderColor: '#C0C0C8'
	},

	// General
	background2: '#E0E0E8'

};

// Apply the theme
Highcharts.setOptions(Highcharts.theme);
</script>

%angular
<form id="filters">
    <div class="row form-group">
        <label for="date_mode" class="col-sm-2 col-form-label">Mode: </label>
        <div class="col-sm-3">
            <select id="date_mode" class="form-control" ng-model="date_mode">
            <option value="-1" ng-selected=-1>SELECT DATE MODE:</option>
            <option value="1">DAY</option>
            <option value="2">MONTH</option>
            </select>
        </div>
    </div>
    <div id="div_month" class="row form-group" style="display:none">
        <label for="date_month" class="col-sm-2 col-form-label">Month: </label>
        <div class="col-sm-3">
            <input id="date_month" type="month" class="form-control" ng-model="date_month"/>
        </div>
    </div>
    <div id="div_day" class="row form-group">
        <label for="date_day" class="col-sm-2 col-form-label">Date: </label>
        <div class="col-sm-3">
            <input id="date_day" type="date" class="form-control" ng-model="date_day"/>
        </div>
    </div>
    <div class="row form-group">
        <label for="event_code_filter" class="col-sm-2 col-form-label">EventRootCode: </label>
        <div class="col-sm-4">
            <select id="event_code_filter" class="form-control" ng-model="event_code_filter">
            </select>
        </div>
    </div>
    <input id="date_month_parsed" type="text" class="form-control" ng-model="date_month_parsed" style="display:none"/>
    <input id="date_day_parsed" type="text" class="form-control" ng-model="date_day_parsed" style="display:none"/>
    <button type="submit" class="btn btn-primary" ng-click="z.angularBind('date_mode',date_mode,'20171223-131255_1307712631');z.angularBind('date_day_parsed',date_day_parsed,'20171223-131255_1307712631');z.angularBind('date_month_parsed',date_month_parsed,'20171223-131255_1307712631');z.angularBind('event_code_filter',event_code_filter,'20171223-131255_1307712631');z.runParagraph('20171223-131139_365208091');z.runParagraph('20171223-131255_1307712631');z.runParagraph('20171227-155110_1278707108')">Load Data</button>
</form>

<script>
    function getEventCodeList(){
        var json_text=JSON.parse($("#event_code_list").text())
        $('#event_code_filter').find('option').remove()
        $('#event_code_filter').append($('<option>', {
                value: -1,
                text: "SELECT EVENT TYPE:",
                selected: "selected"
            }));
        for (i = 0; i < json_text.length; i++){
            $('#event_code_filter').append($('<option>', {
                value: json_text[i].values[0],
                text: json_text[i].values[1]
            }));
        }
    }

    $("#event_code_filter").focus(function(){getEventCodeList()})
    $("#date_mode").click(function(){
        if ($("#date_mode").val()==="2"){
            $("#div_day").hide()
            $("#div_month").show()
        }else{
            $("#div_day").show()
            $("#div_month").hide()
        }
    })

    $("#date_day").change(function(){
        var scope = angular.element($(this)).scope();
        scope.$apply(function(){
            scope.date_day_parsed = $("#date_day").val().replace(/-/g,"");
        })
    })

    $("#date_month").change(function(){
        var scope = angular.element($(this)).scope();
        scope.$apply(function(){
            scope.date_month_parsed = $("#date_month").val().substr(0,7).replace(/-/g,"");
        })        
    })    

</script>

%angular
<script>
function show_event_details(data,countryCode){
    var data=data
    $('#event_date').text(data.date)
    $('#event_country_code').text(countryCode)
    $('#event_source').text(data.source)
    $('#event_source').attr("href",data.source)
    $('#event_tone').text(data.tone)
    $('#event_nbmentions').text(data.y)
    var scope = angular.element($("#details_id")).scope();
    scope.$apply(function(){
        scope.event_id = data.event_id.toString();
    })
    }
</script>

%angular

<script>
function launch_top_events(countryCode,countryName) {
    var json_text=JSON.parse($("#event_details").text())
    var data_temp = [];
    var categories_temp =[];
    for (i = 0; i < json_text.length; i++){
        if (json_text[i].values[2]===countryCode){
            var dict={}
            dict["event_id"]=json_text[i].values[0]
            dict["date"]=json_text[i].values[1]
            dict["y"]=parseFloat(json_text[i].values[4])
            dict["source"]=json_text[i].values[5]
            dict["tone"]=json_text[i].values[3]
            dict["color"]=parseFloat(dict["tone"]) >0 ? "#7cb5ec" : "#910000"
            data_temp.push(dict);
            categories_temp.push( dict["event_id"].toString());
        }
    }
    var data = data_temp
    var categories = categories_temp
    //var data=data_temp.slice(0,10);
    //console.log(data)
    //var categories=categories_temp.slice(0,10);

   Highcharts.chart('top_events', {
    	chart: {
        	type: 'bar',
        },
        title: {
            text: countryName
        },        
        plotOptions: {
        series: {
            allowPointSelect: true,
        },        
        	bar: {
                cursor: 'pointer',
            point: {
                events: {
                    click: function () {
                        show_event_details(this,countryCode);
                    }
                }
            }, 
            },
        },
        tooltip: {
            formatter: function() {
                return '<b>Source:</b><span style="color:blue"> ' + this.point.source + '</span><br/><b>Tone:</b> '+this.point.tone + '<br/><b>#Mentions:</b> '+this.point.y;
            }
        },          
        xAxis: {
            categories: categories,
            title: {
                text: null
            }
        },        
        series: [{
            name: "event ID",
            data: data
		}]
    });
};
</script>

%angular
<button type="submit" class="btn btn-primary" ng-click="z.runParagraph('20171226-120644_1309894606');z.runParagraph('20171226-155455_1029848397');z.runParagraph('20171230-013153_736795064')">Load Library</button>
<button type="submit" class="btn btn-primary btn-success" onclick="carto_mentions();">Show Results</button>
<script>
// Prepare demo data
// Data is joined to map using value of 'hc-key' property by default.
// See API docs for 'joinBy' for more info on linking data and map.
function carto_mentions(){
        var json_text=JSON.parse($("#carto_mentions").text())
        var countries = [];
        for (i = 0; i < json_text.length; i++){
            countries.push([])
            countries[i][0] = json_text[i].values[0];
            countries[i][1] = json_text[i].values[1];
        }
data=countries
//console.log(data)
// Create the chart
Highcharts.mapChart('container-mentions', {
    chart: {
        map: 'custom/world-lowres',
    },

    title: {
        text: 'Events: '+ $("#event_code_filter option:selected").text()
    },

    credits:false,

    subtitle: {
        text: 'Source map: <a href="http://code.highcharts.com/mapdata/custom/world-lowres.js">World, Miller projection, low resolution</a>'
    },

        plotOptions: {
            series: {
                events: {
                    click: function (e) {
                        launch_top_events(e.point["hc-key"],e.point.name);
                    }
                }
            }
        },

    mapNavigation: {
        enabled: true,
        buttonOptions: {
            verticalAlign: 'bottom'
        },
        enableMouseWheelZoom: false
    },

    colorAxis: {
        min: 1,
        type:'logarithmic'
    },

    series: [{
        cursor: 'pointer',
        data: data,
        joinBy: ['iso-a2', "hc-key"],
        name: 'Country',
        borderColor: 'black',
        borderWidth: 0.2,
        states: {
            hover: {
                color: '#BADA55',
                borderWidth: 1
            },
        },
        dataLabels: {
            enabled: true,
            format: '{point.name}'
        }
    }]
});

}
</script>

