<button type="submit" class="btn btn-primary" ng-click="z.runParagraph('20171226-120644_1309894606')">Load Library</button>
<button type="submit" class="btn btn-primary btn-success" onclick="carto_impact();" ng-click="z.runParagraph('20171227-105616_236604505');z.runParagraph('20171226-215110_1953155161')">Show Results</button>
<script>
// Prepare demo data
// Data is joined to map using value of 'hc-key' property by default.
// See API docs for 'joinBy' for more info on linking data and map.
function carto_impact(){
        var json_text=JSON.parse($("#country_impact").text())
        var data_map = new Array();
        var data_line = new Array();
        var origin=$("#event_country_code").text()

        for (i = 0; i < json_text.length; i++){
            data_map.push(new Array(2))
            data_line.push({})
            data_map[i][0] = json_text[i].values[0];
            data_map[i][1] = json_text[i].values[3];
            data_line[i]["id"] = json_text[i].values[0];
            data_line[i]["lat"] = json_text[i].values[1];
            data_line[i]["lon"] = json_text[i].values[2];
            data_line[i]["count"] = json_text[i].values[3];
        }
data=data_map
//console.log(data_line)
// Create the data
Highcharts.mapChart('container-impact', {
    chart: {
        map: 'custom/world-lowres',
    },

    credits:false,

    title: {
        text: 'Impact on countries'
    },

    subtitle: {
        text: 'Source map: <a href="http://code.highcharts.com/mapdata/custom/world-lowres.js">World, Miller projection, low resolution</a>'
    },

    mapNavigation: {
        enabled: true,
        buttonOptions: {
            verticalAlign: 'bottom'
        },
        enableMouseWheelZoom: false
    },

    colorAxis: {
        min: 1,
        type: 'logarithmic',
        minColor: '#ffe6e6',
        maxColor: '#b30000',
    },

    series: [{
        cursor: 'pointer',
        data: data,
        joinBy: ['iso-a2', "hc-key"],
        name: 'Impacted country',
        borderColor: 'black',
        borderWidth: 0.2,
        states: {
            hover: {
                color: '#BADA55',
                borderWidth: 1
            },
        },
        dataLabels: {
            enabled: true,
            format: '{point.name}'
        }
    },
    {
        // Specify points using lat/lon
        type: 'mappoint',
        name: 'Countries',
        visible: false,
        color: Highcharts.getOptions().colors[1],
        data: data_line
    } 
    ],

});

function getlinewidth(nb) {
    var nb = parseInt(nb)
    return 1 + Math.log(nb)/2
  }

var chart=$('#container-impact').highcharts()
origin_point=chart.get(origin)

var destinations=[]
for (i = 0; i<chart.series[1].data.length;i++){
    var line=chart.series[1].data[i]
    if (line.id!==origin){
        destination={}
        destination["id"]=line.id
        destination["path"]=pointsToPath(origin_point,line)
        destination["lineWidth"]=getlinewidth(line.count)
        destination["color"]=Highcharts.getOptions().colors[1]
        destinations.push(destination)
    }
}

function pointsToPath(from, to, invertArc) {
/*    var arcPointX = (from.x + to.x) / (invertArc ? 0 : 2),
        arcPointY = (from.y + to.y) / (invertArc ? 0 : 2);*/
var arcPointX = ((from.x + to.x) * 1.05) / 2,
  arcPointY = ((from.y + to.y) * 1.05) / 2;
return 'M' + from.x + ',' + from.y + 'Q' + arcPointX + ' ' + arcPointY + ',' + to.x + ' ' + to.y;
}

chart.addSeries({
  name: 'Impact',
  type: 'mapline',
  data: destinations,
  visible: true,
});

}
</script>

