<style>
.video {height: 600px}
.video img {position: absolute; display: block; right: 0; left: 0; width: 800px; height: 600px; z-index: 20; cursor: pointer; margin:auto }
.video:after { content: ""; position: absolute; display: block; 
    background: url(play-button.png) no-repeat 0 0; 
    top: 45%; left: 45%; width: 46px; height: 36px; z-index: 30; cursor: pointer; } 
.video iframe { position: absolute; right: 0; left: 0; width: 800px; height: 600px; margin:auto }

/* image poster clicked, player class added using js */
.video.player img { display: none; }
.video.player:after { display: none; }
</style>

<center>
<div class="video">
    <img src="https://decemberprojects.files.wordpress.com/2012/12/the-end.jpeg">
    <!--<iframe  src="https://www.youtube.com/embed/KdKkRRf5uE8?rel=0&amp;showinfo=0&autoplay=1" frameborder="0" gesture="media" allow="encrypted-media" allowfullscreen ></iframe>-->
</div>
</center>



<script>
$(function() { 
    var videos  = $(".video");

        videos.on("click", function(){
            var elm = $(this),
                conts   = elm.contents(),
                le      = conts.length,
                ifr     = null;

            for(var i = 0; i<le; i++){
              if(conts[i].nodeType == 8) ifr = conts[i].textContent;
            }

            elm.addClass("player").html(ifr);
            elm.off("click");
        });
});
</script>
