def toDouble(s : String): Double = {
    if (s.isEmpty) 0  
    else {
        try {
            s.toDouble
        } catch {
            case e: Exception => 0
        }
    }
}

def toInt(s : String): Int = {
    if (s.isEmpty) 0  
    else {
        try {
            s.toInt
        } catch {
            case e: Exception => 0
        }
    }
}

def toBigInt(s : String): BigInt = {
    if (s.isEmpty) BigInt(0) 
